// This node calculates the timestamp for one hour ago.

// Get the current date and time
const oneHourAgo = new Date();

// Subtract one hour from the current time
oneHourAgo.setHours(oneHourAgo.getHours() - 30);

// Format the date into the ISO string format required by the API (e.g., "2025-07-18T12:09:00.000Z")
const timestamp = oneHourAgo.toISOString();

// Return the timestamp so the next node can use it
return [{
  json: {
    start_date_min: timestamp
  }
}];

// This node filters out any posts that contain a 'banned' tag.

const itemsToKeep = [];
const excludedTags = ['Daily', 'Crypto', 'Sports', 'Games', 'Weather' ];

for (const item of items) {
  const post = item.json;

  // Assume the post is good unless a bad tag is found
  let hasExcludedTag = false;

  if (post.tags && Array.isArray(post.tags)) {
    // Check if ANY of the post's tags are in our excluded list
    hasExcludedTag = post.tags.some(tag => excludedTags.includes(tag.label));
  }

  // If no excluded tags were found, keep the item
  if (!hasExcludedTag) {
    itemsToKeep.push(item);
  }
}

// Return only the filtered items
return itemsToKeep;

// This node formats the final Telegram message, handles missing tags, and truncates long messages.

for (const item of items) {
  // The full post data is in the 'json' property of each item
  const post = item.json;
  const TELEGRAM_LIMIT = 4096;
  const TRUNCATION_NOTICE = "\n\n_[... and more questions, message truncated due to length]_";

  // Helper function to format the price change with an emoji and percentage
  const formatPriceChange = (change) => {
    if (typeof change !== 'number' || isNaN(change)) {
      return '*1h Change:* N/A';
    }
    const percentage = (change * 100).toFixed(2);
    if (change > 0) return `ðŸ“ˆ *1h Change:* +${percentage}%`;
    if (change < 0) return `ðŸ“‰ *1h Change:* ${percentage}%`;
    return `âž– *1h Change:* 0.00%`;
  };

  // Helper function for a cleaner date format
  const formatDate = (dateInput) => {
    if (!dateInput) return 'N/A';
    const d = new Date(dateInput);
    const year = d.getUTCFullYear();
    const month = String(d.getUTCMonth() + 1).padStart(2, '0');
    const day = String(d.getUTCDate()).padStart(2, '0');
    const hours = String(d.getUTCHours()).padStart(2, '0');
    const minutes = String(d.getUTCMinutes()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes} UTC`;
  };

  // **FIX for TAGS:** Safely checks if post.tags exists and is an array before processing.
  const tags = (post.tags && Array.isArray(post.tags)) 
    ? post.tags.map(tag => `#${tag.label.replace(/[\s-]/g, '')}`).join(' ') 
    : '';

  // --- Build the message ---

  let message = `ðŸ“Š *${post.title.trim()}*\n`;
  message += `ðŸ”— [View on Polymarket](https://polymarket.com/market/${post.slug})\n\n`;

  if (tags) {
    message += `*Tags:* ${tags}\n`;
  }
  message += `*Published:* ${formatDate(post.createdAt)}\n`;
  message += `\n------------------------------------\n`;

  // Loop through each question (market) and add its details
  let questionsAdded = 0;
  if (post.markets && Array.isArray(post.markets)) {
    for (const q of post.markets) {
      // Safely parse the outcomePrices, which is a string
      let yesPrice = 'N/A';
      if (q.outcomePrices && typeof q.outcomePrices === 'string') {
          try {
            const outcomePrices = JSON.parse(q.outcomePrices);
            yesPrice = parseFloat(outcomePrices[0]);
          } catch(e) {
            // Keep yesPrice as 'N/A' if JSON is invalid
          }
      }

      const questionBlock = `\nâ“ *${q.question.trim()}*\n`
        + `   - ðŸ’² *Yes Price:* \`${yesPrice}\`\n`
        + `   - ðŸ”„ *Last Trade:* \`${q.lastTradePrice || 'N/A'}\`\n`
        + `   - ${formatPriceChange(q.oneHourPriceChange)}\n`
        + `   - ðŸ *End Date:* ${formatDate(q.endDate)}\n`;

      // **FIX for LENGTH:** Check if adding this block would exceed the limit
      if (message.length + questionBlock.length + TRUNCATION_NOTICE.length > TELEGRAM_LIMIT) {
        break; // Stop adding more questions
      }

      message += questionBlock;
      questionsAdded++;
    }
  }

  // Add a truncation notice if not all questions were included
  if (post.markets && questionsAdded < post.markets.length) {
    message += TRUNCATION_NOTICE;
  }

  // Add the final formatted message to the item's JSON
  item.json.telegram_message = message;
}

// Return the items with the new 'telegram_message' field
return items;
